      assert.equal(balance.toString(), ether);
    })
    it('should error if its the shipper', async () => {
      let ether = web3.toBigNumber(web3.toWei('.1', 'Ether'));
      const promise = contract.pay({ value: ether, from: _shipper });
      await expectThrow(promise);
    })
    it('should log that it was paid', async () => {
      let ether = web3.toBigNumber(web3.toWei('.1', 'Ether'));
      let tx = await contract.pay({ value: ether, from: _astronaut });
      console.log(findEvent(tx, 'Paid'))
      assert(findEvent(tx, 'Paid'));
    })
  })
  describe('Constructor Stage tests', function() {
    it('should set an arbiter', async function() {
        let arbiter = await contract.arbiter.call();
        assert.deepEqual(arbiter, _arbiter);
    });
    it('should set an astronaut', async function() {
        let astronaut = await contract.astronaut.call();
        assert.deepEqual(astronaut, _astronaut);
    });
    it('should set an shipper', async function() {
        let shipper = await contract.shipper.call();
        assert.deepEqual(shipper, _shipper);
    });
  })
});

async function expectThrow(promise) {
  const errMsg = 'Expected throw not received';
  try {
    await promise;
  } catch (err) {
    assert(err.toString().includes('invalid opcode'), errMsg);
    return;
  }
  assert.fail(errMsg);
}

function findEvent(transaction, evt) {
  let event = transaction.logs.filter(({ event }) => event === evt)[0];
  if(!event) throw `Remember to call ${evt} event!`;
  return event.args;
}

